import {
  Dialog,
  withStyles,
  Box,
  Typography,
  makeStyles,
  List,
  ListItem,
} from "@material-ui/core";
import { GoogleLogin } from "react-google-login";
import { useContext, useState } from "react";
import { AccountContext } from "../../context/AccountProvider";
import { clientId } from "../../constant/data";

//Code
const useStyles = makeStyles({
  component: {
    display: "flex",
  },
  lefComponent: {
    padding: "56px 0 56px 56px",
  },
  qrCode: {
    height: 264,
    width: 264,
    padding: "50px 50px 0 50px",
  },
  title: {
    fontSize: 26,
    marginBottom: 25,
    fontFamily:
      "Segoe UI, Helvetica Neue, Lucida Grande, Arial, Ubuntu, Cantarell, Fira Code",
    fontWeight: 300,
  },
  list: {
    "& > *": {
      fontSize: 18,
      padding: 0,
      marginBottom: 15,
      lineHeight: "28px",
      color: "#4a4a4a",
    },
  },
});

const style = {
  dialogPaper: {
    height: "95%",
    width: "100%",
    marginTop: "12%",
    boxShadow: "none",
    borderRadius: 0,
    // maxHeight: "100%",
    maxWidth: "48%",
  },
};

const Login = ({ classes }) => {
  const classname = useStyles();
  const qrUrl = "http://www.ginifab.com/feeds/qr_code/img/qrcode.jpg";

  const { account, setAccount } = useContext(AccountContext);

  const loginSuccess = (res) => {
    console.log("Login SuccessFul!", res.profileObj);
    setAccount(res.profileObj);
  };
  const loginFailure = () => {
    console.log("Login Failed!");
  };

  return (
    <Dialog
      open={true}
      classes={{ paper: classes.dialogPaper }}
      BackdropProps={{ style: { backgroundColor: "unset" } }}
    >
      <Box></Box>
      <Box className={classname.component}>
        <Box className={classname.lefComponent}>
          <Typography className={classname.title}>
            Dang nhap bang What Apps
          </Typography>
          <List className={classname.list}>
            <ListItem>1. Mở What Apps trên điện thoại của bạn</ListItem>
            <ListItem>
              2. Chọn Menu hoặc Cài đặt và chọn Kết nối thiết bị
            </ListItem>
            <ListItem>3. Đưa điện thoại ra trước màn hình và quét</ListItem>
          </List>
        </Box>
        <Box style={{ position: "relative" }}>
          <img
            src={"../../img/LogoRabbit.png"}
            alt="qr"
            className={classname.qrCode}
          />
          <Box style={{ position: "absolute", left: "15%" }}>
            <GoogleLogin
              clientId={clientId}
              buttonText="Đăng nhập bằng Google"
              isSignedIn={true}
              onSuccess={loginSuccess}
              onFailure={loginFailure}
              cookiePolicy={"single_host_origin"}
            />
          </Box>
        </Box>
      </Box>
    </Dialog>
  );
};

export default withStyles(style)(Login);
